mapper.insert:
INSERT INTO GND_APT_INFO (
    apt_no
    , apt_nm
    , building_type
    , apt_type
    , location_id
    , zipcode
    , jibun_addr
    , road_addr
    , dong_cnt
    , ho_cnt
    , tel_no
    , remark
    , reg_id
    , reg_dt
    , update_id
    , update_dt
) values (
    #{aptNo}
    , #{aptNm}
    , #{buildingType}
    , #{aptType}
    , #{locationId}
    , #{zipcode}
    , #{jibunAddr}
    , #{roadAddr}
    , #{dongCnt}
    , #{hoCnt}
    , #{telNo}
    , #{remark}
    , #{regId}
    , now()
    , #{updateId}
    , now()
)

mapper.update:
UPDATE GND_APT_INFO
apt_nm = #{aptNm}, building_type = #{buildingType}, apt_type = #{aptType}, location_id = #{locationId}, zipcode = #{zipcode}, jibun_addr = #{jibunAddr}, road_addr = #{roadAddr}, dong_cnt = #{dongCnt}, ho_cnt = #{hoCnt}, tel_no = #{telNo}, remark = #{remark}, update_id = #{updateId}, update_dt = now()
WHERE apt_no = #{aptNo}
