mapper.insert:
INSERT INTO GND_COMM_POINT (
    comm_point_id
    , service_point_id
    , device_id
    , device_serial
    , remark
    , reg_id
    , reg_dt
    , update_id
    , update_dt
) values (
    #{commPointId}
    , #{servicePointId}
    , #{deviceId}
    , #{deviceSerial}
    , #{remark}
    , #{regId}
    , now()
    , #{updateId}
    , now()
)

mapper.update:
UPDATE GND_COMM_POINT
service_point_id = #{servicePointId}, device_id = #{deviceId}, device_serial = #{deviceSerial}, remark = #{remark}, update_id = #{updateId}, update_dt = now()
WHERE comm_point_id = #{commPointId}
