mapper.insert:
INSERT INTO GND_DEVICE_INFO (
    device_id
    , device_serial
    , location_id
    , device_oid
    , comm_type
    , device_type
    , node_type
    , ip
    , port
    , allow_yn
    , remark
    , parent_gw_id
    , reg_id
    , reg_dt
    , dest_dt
) values (
    #{deviceId}
    , #{deviceSerial}
    , #{locationId}
    , #{deviceOid}
    , #{commType}
    , #{deviceType}
    , #{nodeType}
    , #{ip}
    , #{port}
    , #{allowYn}
    , #{remark}
    , #{parentGwId}
    , #{regId}
    , now()
    , DATE_FORMAT(#{destDt},'${prop.dt}')
)

mapper.update:
UPDATE GND_DEVICE_INFO
device_serial = #{deviceSerial}, location_id = #{locationId}, device_oid = #{deviceOid}, comm_type = #{commType}, device_type = #{deviceType}, node_type = #{nodeType}, ip = #{ip}, port = #{port}, allow_yn = #{allowYn}, parent_gw_id = #{parentGwId}, reg_dt = now()
WHERE device_id = #{deviceId}
