    model_nm
    , device_type
    , vendor_seq
    , reg_id
    , reg_dt
    , update_id
    , update_dt
) values (
    #{modelNm}
    , #{deviceType}
    , #{vendorSeq}
    , #{regId}
    , now()
    , #{updateId}
    , now()
)

mapper.update:
UPDATE GND_DEVICE_MODEL
model_nm = #{modelNm}, device_type = #{deviceType}, vendor_seq = #{vendorSeq}, update_id = #{updateId}, update_dt = now()
WHERE model_seq = #{modelSeq}
