mapper.insert:
INSERT INTO GND_MEASUREMENT_POINT (
    service_point_id
    , meter_id
    , meter_serial
    , remark
    , reg_id
    , reg_dt
    , update_id
    , update_dt
) values (
    #{servicePointId}
    , #{meterId}
    , #{meterSerial}
    , #{remark}
    , #{regId}
    , now()
    , #{updateId}
    , now()
)

mapper.update:
UPDATE GND_MEASUREMENT_POINT
service_point_id = #{servicePointId}, meter_id = #{meterId}, meter_serial = #{meterSerial}, remark = #{remark}, update_id = #{updateId}, update_dt = now()
WHERE measurement_point_id = #{measurementPointId}
