mapper.insert:
INSERT INTO GND_METER_INFO (
    meter_serial
    , device_id
    , meter_type
    , energy_type
    , meter_phase
    , model_seq
    , ip
    , port
    , cosem_device_name
    , prog_id
    , prog_version
    , acon
    , rcon
    , pcon
    , billing_day
    , lp_interval
    , avg_vol_cur_interval
    , inst_vol_cur_interval
    , net_metering
    , location_id
    , mtime
    , itime
    , install_dt
    , reg_dt
    , dest_dt
    , mfg_dt
) values (
    #{meterSerial}
    , #{deviceId}
    , #{meterType}
    , #{energyType}
    , #{meterPhase}
    , #{modelSeq}
    , #{ip}
    , #{port}
    , #{cosemDeviceName}
    , #{progId}
    , #{progVersion}
    , #{acon}
    , #{rcon}
    , #{pcon}
    , #{billingDay}
    , #{lpInterval}
    , #{avgVolCurInterval}
    , #{instVolCurInterval}
    , #{netMetering}
    , #{locationId}
    , DATE_FORMAT(#{mtime},'${prop.dt}')
    , DATE_FORMAT(#{itime},'${prop.dt}')
    , DATE_FORMAT(#{installDt},'${prop.dt}')
    , now()
    , #{destDt}
    , #{mfgDt}
)

mapper.update:
UPDATE GND_METER_INFO
meter_serial = #{meterSerial}, device_id = #{deviceId}, meter_type = #{meterType}, energy_type = #{energyType}, meter_phase = #{meterPhase}, model_seq = #{modelSeq}, ip = #{ip}, port = #{port}, cosem_device_name = #{cosemDeviceName}, prog_id = #{progId}, prog_version = #{progVersion}, acon = #{acon}, rcon = #{rcon}, pcon = #{pcon}, billing_day = #{billingDay}, lp_interval = #{lpInterval}, avg_vol_cur_interval = #{avgVolCurInterval}, inst_vol_cur_interval = #{instVolCurInterval}, net_metering = #{netMetering}, location_id = #{locationId}, mtime = DATE_FORMAT(#{mtime},'${prop.dt}'), itime = DATE_FORMAT(#{itime},'${prop.dt}'), install_dt = DATE_FORMAT(#{installDt},'${prop.dt}'), dest_dt = DATE_FORMAT(#{destDt},'${prop.dt}'), mfg_dt= #{mfgDt},
WHERE meter_id = #{meterId}
