mapper.insert:
INSERT INTO GND_MODEM_INFO (
    device_id
    , device_serial
    , modem_type
    , node_kind
    , model_seq
    , fw_version
    , fw_build
    , hw_version
    , modem_mode
    , reg_dt
    , update_dt
) values (
    #{deviceId}
    , #{deviceSerial}
    , #{modemType}
    , #{nodeKind}
    , #{modelSeq}
    , #{fwVersion}
    , #{fwBuild}
    , #{hwVersion}
    , #{modemMode}
    , now()
    , now()
)

mapper.update:
UPDATE GND_MODEM_INFO
device_serial = #{deviceSerial}, modem_type = #{modemType}, node_kind = #{nodeKind}, model_seq = #{modelSeq}, fw_version = #{fwVersion}, fw_build = #{fwBuild}, hw_version = #{hwVersion}, modem_mode = #{modemMode}, update_dt = now()
WHERE device_id = #{deviceId}
